	,Order_Product_Seq	= #{orderProductSeq}
	, UPDATE_ID 		= #{updateId}
	, UPDATE_DT 		= NOW()	
WHERE
	Bottle_ID = #{bottleId}

mapper.select:
SELECT A.*, B.Customer_Nm
FROM (
	SELECT

	FROM
		TB_Bottle TB, TB_CODE TC, TB_Gas TG
	WHERE
		TB.Delete_YN = 'N'

	AND Bottle_ID	 LIKE CONCAT('%', CONCAT(#{searchBottleId}, '%'))


	AND TG.Gas_ID=  #{searchGasId}


	AND date_format(Bottle_Charge_Dt,'%Y/%m/%d')  between  #{searchChargeDtFrom} AND  #{searchChargeDtEnd} 


	AND Bottle_Sales_Yn =  #{searchSalesYn}

	AND TB.Bottle_Work_CD = TC.CD_ID	
	AND TB.Gas_ID = TG.Gas_Id
) A left outer join TB_Customer B on A.Customer_ID = B.Customer_ID														
ORDER BY Create_Dt DESC 
Limit #{startRow}, #{rowPerPage}

SELECT A.*, B.Customer_Nm FROM ( SELECT
FROM TB_Bottle TB, TB_CODE TC, TB_Gas TG WHERE TB.Delete_YN = 'N'
AND Bottle_ID LIKE CONCAT('%', CONCAT(#{searchBottleId}, '%'))
AND TG.Gas_ID= #{searchGasId}
AND date_format(Bottle_Charge_Dt,'%Y/%m/%d') between #{searchChargeDtFrom} AND #{searchChargeDtEnd}
AND Bottle_Sales_Yn = #{searchSalesYn}
AND TB.Bottle_Work_CD = TC.CD_ID AND TB.Gas_ID = TG.Gas_Id ) A left outer join TB_Customer B on A.Customer_ID = B.Customer_ID ORDER BY Create_Dt DESC

SELECT
	Count(Bottle_id)
FROM
	TB_Bottle
WHERE
	Delete_YN = 'N'

AND Bottle_ID	 LIKE CONCAT('%', CONCAT(#{searchBottleId}, '%'))


AND Gas_ID=  #{searchGasId}


AND date_format(Bottle_Charge_Dt,'%Y/%m/%d')  between  #{searchChargeDtFrom} AND  #{searchChargeDtEnd} 


AND Bottle_Sales_Yn =  #{searchSalesYn}

mapper.insert:
INSERT INTO TB_Bottle_Hist
SELECT b.history_seq,a.*
	FROM TB_Bottle a,
	(
		SELECT bottle_id, count(bottle_id)+1 history_seq 
		FROM TB_Bottle_Hist 
		WHERE Bottle_Id=#{bottleId}
	) b 
WHERE a.Bottle_Id =#{bottleId}
