INSERT INTO TB_Bottle_Hist
SELECT b.history_seq,a.*
	FROM TB_Bottle a,
	(
		SELECT bottle_id, count(bottle_id)+1 history_seq 
		FROM TB_Bottle_Hist 
		WHERE Bottle_Id=#{bottleId}
	) b 
WHERE a.Bottle_Id =#{bottleId}

INSERT INTO TB_Bottle_Hist SELECT b.history_seq,a.* FROM TB_Bottle a, ( SELECT c.bottle_id, IFNULL(count(d.bottle_id)+1,1) history_seq FROM TB_Bottle c Left Join TB_Bottle_Hist d ON c.Bottle_Id=d.bottle_id WHERE c.Bottle_Id in (
#{item.bottleId}
) GROUP BY C.Bottle_Id ) b WHERE a. Bottle_ID = b.Bottle_Id

