mapper.select:
SELECT A.*, B.Customer_Nm
FROM (
	SELECT

	FROM
		TB_Bottle TB, TB_CODE TC, TB_Product TG
	WHERE
		TB.Delete_YN = 'N'

	AND Bottle_ID	 LIKE CONCAT('%', CONCAT(#{searchBottleId}, '%'))


	AND TG.Product_ID=  #{searchProductId}


	AND date_format(Bottle_Charge_Dt,'%Y/%m/%d')  between  #{searchChargeDtFrom} AND  #{searchChargeDtEnd} 


	AND Bottle_Work_CD =  #{bottleWorkCd}

	AND TB.Bottle_Work_CD = TC.CD_ID	
	AND TB.Product_ID = TG.Product_Id
) A left outer join TB_Customer B on A.Customer_ID = B.Customer_ID														
ORDER BY Bottle_Work_DT DESC 
Limit #{startRow}, #{rowPerPage}

SELECT A.*, B.Customer_Nm FROM ( SELECT
FROM TB_Bottle TB, TB_CODE TC, TB_ProductTG WHERE TB.Delete_YN = 'N'
AND Bottle_ID LIKE CONCAT('%', CONCAT(#{searchBottleId}, '%'))
AND TG.Product_ID= #{searchProductId}
AND date_format(Bottle_Charge_Dt,'%Y/%m/%d') between #{searchChargeDtFrom} AND #{searchChargeDtEnd}
AND Bottle_Work_CD = #{bottleWorkCd}
AND TB.Bottle_Work_CD = TC.CD_ID AND TB.Product_ID = TG.Product_Id ) A left outer join TB_Customer B on A.Customer_ID = B.Customer_ID ORDER BY Bottle_Work_DT DESC

SELECT
	Count(Bottle_id)
FROM
	TB_Bottle
WHERE
	Delete_YN = 'N'

AND Bottle_ID	 LIKE CONCAT('%', CONCAT(#{searchBottleId}, '%'))


	AND Product_ID=  #{searchProductId}


AND date_format(Bottle_Charge_Dt,'%Y/%m/%d')  between  #{searchChargeDtFrom} AND  #{searchChargeDtEnd} 


AND Bottle_Work_CD =  #{bottleWorkCd}
