mapper.insert:
INSERT INTO TB_Cash_Flow(

) VALUES (
	#{customerId}
	, #{incomeAmount}	
	, #{receivableAmount}	
	, #{incomeWay}		
	, 'N'	
	, #{createId}			
	, NOW()
	, NULL
	, NOW()
)

mapper.update:
UPDATE TB_Cash_Flow
SET
	  Income_Amount		= #{incomeAmount}
	, Receivable_Amount	= #{receivableAmount}
	, Income_Way		= #{incomeWay}
	, UPDATE_ID 		= #{updateId}
	, UPDATE_DT 		= NOW()
WHERE
	Cash_Flow_Seq 		= #{cashFlowSeq}

mapper.select:
SELECT

FROM
	TB_Cash_Flow
WHERE
	Customer_ID =  #{customerId}	
AND
	Delete_YN 	= 'N'		

AND date_format(Create_Dt,'%Y/%m/%d')  between  #{searchCreateDtFrom} AND  #{searchCreateDtEnd} 

ORDER BY Create_Dt DESC 
Limit #{startRow}, #{rowPerPage}
