mapper.insert:
INSERT INTO TB_USER(

) VALUES (
	  #{userId}
	, #{memberCompSeq}
	, #{userNm}
	, #{userPhone}
	, #{userPartCd}
	, #{userAuthority}
	, #{userPasswd}
	, NULL
	, IFNULL(#{deleteYn}, 'N')
	, #{createId}			
	, NOW()
	, NULL
	, NOW()
)

mapper.update:
UPDATE TB_USER
SET
	  User_NM			= #{userNm}
	, User_Phone		= #{userPhone}
	, User_Part_CD		= #{userPartCd}
	, User_Authority	= #{userAuthority}
	, User_Passwd		= #{userPasswd}
	, UPDATE_ID			= #{updateId}
	, UPDATE_DT			= NOW()
WHERE
	USER_ID = #{userId}

mapper.select:
SELECT
FROM TB_USER WHERE delete_yn = 'N'
AND User_NM LIKE CONCAT('%', CONCAT(#{searchUserNm}, '%'))
AND User_Part_CD= #{userPartCd}
ORDER BY Create_Dt DESC Limit #{startRow}, #{rowPerPage}
