mapper.update:
UPDATE TB_Bottle
SET
	Bottle_BarCd		= #{bottleBarCd}
	, Gas_ID			= #{gasId}
	, Bottle_Capa		= #{bottleCapa}
	, Bottle_Create_Dt	= #{bottleCreateDt}
	, Bottle_Charge_Dt	= #{bottleChargeDt}
	, Bottle_Volumn		= #{bottleVolumn}
	, Bottle_Charge_Prss= #{bottleChargePrss}
	, Bottle_Sales_YN	= #{bottleSalesYn}
WHERE
	Bottle_ID = #{bottleId}

UPDATE TB_Bottle
SET
	  Bottle_Work_CD = #{bottleWorkCd}
	, Bottle_Work_ID	= #{bottleWorkId}
	, UPDATE_ID = #{updateId}
	, UPDATE_DT = NOW()
WHERE
	Bottle_ID = #{chBottleId}

mapper.select:
SELECT A.*, B.Customer_Nm FROM ( SELECT
FROM TB_Bottle TB, TB_CODE TC, TB_Gas TG WHERE TB.Delete_YN = 'N'
AND Bottle_ID LIKE CONCAT('%', CONCAT(#{searchBottleId}, '%'))
AND TG.Gas_ID= #{searchGasId}
AND date_format(Bottle_Charge_Dt,'%Y/%m/%d') between #{searchChargeDtFrom} AND #{searchChargeDtEnd}
AND TB.Bottle_Work_CD = TC.CD_ID AND TB.Gas_ID = TG.Gas_Id ) A left outer join TB_Customer B on A.Customer_ID = B.Customer_ID ORDER BY Create_Dt DESC Limit #{startRow}, #{rowPerPage}

SELECT Count(Bottle_id) FROM TB_Bottle WHERE Delete_YN = 'N'
AND Bottle_ID LIKE CONCAT('%', CONCAT(#{searchBottleId}, '%'))
AND Gas_ID= #{searchGasId}
AND date_format(Bottle_Charge_Dt,'%Y/%m/%d') between #{searchChargeDtFrom} AND #{searchChargeDtEnd}
