mapper.insert:
INSERT INTO TB_Cash_Flow(

) VALUES (
	#{customerId}
	, #{incomeAmount}	
	, #{receivableAmount}	
	, #{incomeWay}		
	, 'N'	
	, #{createId}			
	, NOW()
	, NULL
	, NOW()
)

mapper.update:
UPDATE TB_Cash_Flow
SET
	  Income_Amount		= #{incomeAmount}
	, Receivable_Amount	= #{receivableAmount}
	, Income_Way		= #{incomeWay}
	, UPDATE_ID 		= #{updateId}
	, UPDATE_DT 		= NOW()
WHERE
	Cash_Flow_Seq 		= #{cashFlowSeq}

mapper.select:
SELECT Cash_Flow_Seq , TF.Customer_ID , Customer_Nm , Income_Amount , Receivable_Amount , Income_Way , TF.Delete_YN , TF.Create_ID , TF.Create_Dt , TF.Update_ID , TF.Update_Dt FROM TB_Cash_Flow TF, TB_Customer TC WHERE TF.Customer_ID = #{customerId} AND TF.Customer_Id = TC.Customer_Id AND Delete_YN = 'N'
AND date_format(Create_Dt,'%Y/%m/%d %H') between #{searchCreateDtFrom} AND #{searchCreateDtEnd}
ORDER BY TF.Create_Dt DESC Limit #{startRow}, #{rowPerPage}

SELECT Cash_Flow_Seq , TF.Customer_ID , Customer_Nm , Income_Amount , Receivable_Amount , Income_Way , TF.Delete_YN , TF.Create_ID , TF.Create_Dt , TF.Update_ID , TF.Update_Dt FROM TB_Cash_Flow TF, TB_Customer TC WHERE TF.Customer_Id = TC.Customer_Id AND TF.Delete_YN = 'N' AND Customer_Status = 2
AND date_format(TF.Create_Dt,'%Y/%m/%d %H') between #{searchCreateDtFrom} AND #{searchCreateDtEnd}
ORDER BY TF.Create_Dt DESC Limit #{startRow}, #{rowPerPage}
