mapper.update:
UPDATE TB_CUSTOMER
SET
	  Customer_NM			= #{customerNm}
	, Business_Reg_ID		= #{businessRegId}
	, Customer_Rep_Nm		= #{customerRepNm}
	, Customer_Busi_Type	= #{customerBusiType}
	, Customer_Item			= #{customerItem}
	, Customer_Addr			= #{customerAddr}
	, Customer_Phone		= #{customerPhone}
	, Customer_Email		= #{customerEmail}
	, Sales_ID				= #{salesId}
	, Car_YN				= #{carYn}
	, Agency_YN				= #{agencyYn}
	, Parent_Customer_ID	= #{parentCustomerId}
	, ECount_CD				= #{eCountCd}
	, Summary				= #{summary}
	, UPDATE_ID				= #{updateId}
	, UPDATE_DT				= NOW()
WHERE
	CUSTOMER_ID = #{customerId}

UPDATE TB_CUSTOMER
SET
	  Customer_NM		= #{customerNm}
	, Business_Reg_ID	= #{businessRegId}
	, Customer_Rep_Nm	= #{customerRepNm}
	, Customer_Busi_Type= #{customerBusiType}
	, Customer_Item		= #{customerItem}
	, Customer_Addr		= #{customerAddr}
	, Customer_Phone	= #{customerPhone}
	, Customer_Email	= #{customerEmail}
	, UPDATE_ID			= #{updateId}
	, UPDATE_DT			= NOW()
WHERE
	CUSTOMER_ID = #{customerId}

UPDATE TB_CUSTOMER
SET
	  Bottle_Own_Count	= Bottle_Own_Count + #{bottleOwnCount}			
	, UPDATE_ID			= #{updateId}
	, UPDATE_DT			= NOW()
WHERE
	CUSTOMER_ID = #{customerId}

UPDATE TB_CUSTOMER
SET
	  Bottle_Rent_Count	= Bottle_Rent_Count + #{bottleRentCount}			
	, UPDATE_ID			= #{updateId}
	, UPDATE_DT			= NOW()
WHERE
	CUSTOMER_ID = #{customerId}

UPDATE TB_CUSTOMER_PRODUCT_PRICE	
SET 
	Product_Price = #{productPrice}
	, Product_Bottle_Price = #{productBottlePrice}
	, UPDATE_ID			= #{updateId}
	, UPDATE_DT			= NOW()
WHERE
	CUSTOMER_ID = #{customerId}
AND
	Product_ID =  #{productId}
AND
	 Product_Price_Seq = #{productPriceSeq}

mapper.delete:
DELETE FROM TB_CUSTOMER_PRODUCT_PRICE WHERE CUSTOMER_ID in (
#{item.customerId}
)

mapper.select:
