mapper.update:
UPDATE TB_Product
SET
	  Product_Nm = #{productNm}
	, Gas_ID = #{gasId}
	, UPDATE_ID = #{updateId}
	, UPDATE_DT = NOW()
WHERE
	Product_ID = #{productId}

UPDATE TB_Product_Price
SET
	  Product_Price = #{productPrice}
	, product_Capa = #{productCapa}
	, UPDATE_ID = #{updateId}
	, UPDATE_DT = NOW()
WHERE
	Product_ID = #{productId}
AND 
	Product_Price_Seq =  #{productPriceSeq}

UPDATE TB_Product_Price
SET
	  Product_Status = #{productPrice}
WHERE
	Product_ID = #{productId}
AND 
	Product_Price_Seq =  #{productPriceSeq}

UPDATE TB_Product
SET
	  Prodcut_Status = #{productStatus}
	, UPDATE_ID = #{updateId}
	, UPDATE_DT = NOW()
WHERE
	Product_ID = #{productId}
