mapper.select:
SELECT
FROM TB_Daily_Stat_Customer WHERE Customer_Id = #{searchCustomerId}
AND Stat_Dt between #{searchStatDtFrom} AND #{searchStatDtEnd}
ORDER BY Create_Dt DESC

SELECT
FROM TB_Monthly_Stat_Customer WHERE Customer_Id = #{searchCustomerId}
AND Stat_Dt between #{searchStatDtFrom} AND #{searchStatDtEnd}
ORDER BY Create_Dt DESC
