mapper.select:
SELECT
FROM TB_Daily_Stat_Product WHERE Product_Id = #{searchProductId}
AND Stat_Dt between #{searchStatDtFrom} AND #{searchStatDtEnd}
ORDER BY Create_Dt DESC

SELECT
FROM TB_Monthly_Stat_Product WHERE Product_Id = #{searchProductId}
AND Stat_Dt between #{searchStatDtFrom} AND #{searchStatDtEnd}
ORDER BY Create_Dt DESC
